mapper.select:
SELECT id, userId, url, name 
  FROM BOOKMARK_T 
 WHERE id = #{id} 
   AND userId = #{userId}

mapper.update:
UPDATE BOOKMARK_T
   SET url = #{url}
     , name = #{name}
 WHERE id = #{id}
   AND userId = #{userId}

mapper.delete:
DELETE 
  FROM BOOKMARK_T 
 WHERE id = #{id}
   AND userId = #{userId}
