mapper.select:
SELECT *
  FROM (SELECT ROWNUM N, tbl_1.*
          FROM (SELECT id, bnum, writer, context
                     , createDate, cLevel, cRef, cReply_SEQ
                  FROM COMMENT_T
                 WHERE bnum = #{bnum}
                 ORDER BY cRef DESC, cReply_SEQ ASC) tbl_1
		)tbl_2
 WHERE N BETWEEN #{start} AND #{end}

mapper.delete:
DELETE
  FROM COMMENT_T
 WHERE id = #{id}
   AND writer = #{writer}
